void mainImage(out vec4 O, vec2 F) {
    vec2 r = iResolution.xy, u = (F+F-r)/r.y;
    O.rgb*=0.;
    for (float i; i<20.; i++) {
        // Calculate the position of the circle based on the current time
        vec2 circlePos = vec2(sin(iTime*0.3 + i), cos(iTime*0.5 + i));
        // Calculate the distance from the center of the circle to the current pixel
        float circleDistance = .004/(abs(length(u - circlePos)-i*.04)+.005);
        vec3 color = (cos(i+vec3(0,1,2))+1.);
        float animation = smoothstep(.35,.4,abs(abs(mod(iTime,2.)-i*.1)-1.));
        O.rgb += circleDistance * color * animation;
        u *= mat2(cos((iTime+i++)*.03 + vec4(0,33,11,0)));
    }
}
