void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    //consts
    float frequencyConst = 2.0;
    float pi = 3.14159265358979323846264338327950288419716;

    //variables
    float[] alpha = float [] (1.0, 2.0);
    float[] waveLength = float [] (2.0, 4.0);
    int n = 2;
    float func = 0.0;

    vec2 uv = fragCoord / iResolution.xy * 2.0 - 1.0;

    func = alpha[0] * sin(uv.x * frequencyConst * pi / waveLength[0] + iTime);

    //func = smoothstep(0.0, 0.1, func);

    vec3 col = vec3(0.0, 0.0, abs(func));

    fragColor = vec4(col, 1.0);
}
