vec2 trans(vec2 pos){
    vec2 uni = 128.*(2.*pos - iResolution.xy)/iResolution.y;
    return uni;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = trans(fragCoord.xy);
    vec2 oldMouse = trans(abs(iMouse.zw));
    vec2 mouse = trans(iMouse.xy);

    if(iMouse.xy == vec2(0, 0)){
        mouse = 128.*(0.8*vec2(iResolution.x/iResolution.y, 1.) + 0.05*sin(iTime*0.5)*vec2(-1., iResolution.x/iResolution.y));
        oldMouse = -128.*(0.8*vec2(iResolution.x/iResolution.y, 1.) + 0.05*sin(iTime*0.5)*vec2(-1., iResolution.x/iResolution.y));
    }

    vec2 a1 = oldMouse;
    vec2 b1 = mouse;
    vec3 c1 = vec3(0.2, 0.1, 0.8);

    float dist = length(uv - a1) + length(uv - b1) - length(b1 - a1);
    vec3 light = c1/dist;
    vec3 col = 1. - 1./(light + 1.);

    fragColor = vec4(col,1.0);
}
