#define rot(a) mat2(cos(a),sin(a),-sin(a),cos(a))

vec3 render(vec2 p) {
    p*=rot(iTime*.1)*(0.11+.7*pow(smoothstep(0.,.5,abs(.5-fract(iTime*0.01))),3.));
    p.y-=1.1;
    p.x+=.2;
    vec2 ot=vec2(1.);
    float m=1.;
    for (int i=0; i<40; i++) {
        vec2 cp=vec2(p.x,-p.y);
		p=p+cp/dot(p,p)-vec2(0.3,.5);
        p*=.1;
        p*=rot(1.4);
        ot=min(ot,abs(p)+.09*fract(max(abs(p.x),abs(p.y))*0.2+iTime*.1+float(i)*30.02));
        m=min(m,abs(p.y));
    }
    ot=exp(-200.*ot)*2.;
    m=exp(-200.*m);
    return vec3(ot.x,ot.y*.5+ot.x*.3,ot.y)+m*.2;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-iResolution.xy*.5)/iResolution.y;
    vec2 d=vec2(0.,.1)/iResolution.xy;
    vec3 col = render(uv)+render(uv+d.xy)+render(uv-d.xy)+render(uv+d.yx)+render(uv-d.yx);
    fragColor = vec4(col*.2,1.0);
}
