//DAYTONA
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 aspect = fragCoord / iResolution.xy; // falls under background
    vec3 backgroundcolour = vec3(aspect.y, aspect.x, 1.0);

    fragColor = vec4(backgroundcolour, 1.0); 


    float time = iTime * 0.35; // falls under ring, movement
    float movement = 0.9 - cos(time) * 0.7;

    vec2 uvcircle = (fragCoord * 2.0 - iResolution.xy) / iResolution.y; //changes focus
    float d = length(uvcircle); // falls under ring, movement
    float radius = movement;
    float thickness = 0.1;

    d = abs(d);

    float circle = step(radius - thickness, d) - step(radius, d);
    vec3 circlecol = vec3(aspect.x, aspect.y, 1.0);

    // Output to screen
    fragColor = vec4(mix(backgroundcolour, circlecol, circle), 1.0);
}
