void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec4 img = pow(texture(iChannel0,(round((fragCoord+.5)/3.)*3.)/iResolution.xy),vec4(2.2));

    vec4 col = img * vec4(mod(fragCoord.x,3.)-1.,mod(fragCoord.x -1.,3.) - 1.,mod(fragCoord.x -2.,3.) - 1.,0.);
    //vec4 col = img;

    // Output to screen
    fragColor = col;
}
