// GLSL Fragment Shader: Dialogue and Discovery

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1), centered and adjusted for aspect ratio
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    // Time variable for ongoing interaction
    float time = iTime * 0.1;

    // Dialogue pattern: Represents the exchange of questions and answers
    float dialoguePattern = sin(dot(uv, vec2(cos(time), sin(time))) * 20.0) * 0.5 + 0.5;

    // Discovery pattern: Symbolizes the expansion of understanding and curiosity
    float discoveryPattern = cos(dot(uv, vec2(sin(time * 0.5), cos(time * 0.5))) * 40.0) * 0.5 + 0.5;

    // Combine dialogue and discovery patterns
    float combinedPattern = mix(dialoguePattern, discoveryPattern, 0.5);

    // Color mapping to visualize interaction and growth
    vec3 color = vec3(combinedPattern, combinedPattern * 0.8, combinedPattern * 0.6);

    // Output the final color
    fragColor = vec4(color, 1.0);
}
