void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 position = fragCoord / iResolution.xy * 10.0;
    position.x *= iResolution.x / iResolution.y;
    position += iTime * 0.25;
    uint seed = 0x578437adU; // can be set to something else if you want a different set of random values

    if (fragCoord.y < iResolution.y / 2.0) {
        if (fragCoord.x > iResolution.x / 2.0) {
            position *= 5.0;
            float value = perlinNoise(position, 2, 4, seed);
            // value = (value + 1.0) * 0.5;
            fragColor = vec4(vec3(max(0.0,cos(position.x + value))), 1.0);
        } else {
            float value1 = perlinNoise(position, 2, 4, seed);
            float value2 = perlinNoise(position + vec2(value1), 2, 4, seed);
            value2 = (value2 + 1.0) * 0.5;
            fragColor = vec4(vec3(value2), 1.0);
        }
    } else {
        if (fragCoord.x > iResolution.x / 2.0) {
            float value = perlinNoise(position, 2, 4, seed);
            value = (value + 1.0) * 0.5;
            fragColor = vec4(vec3(value), 1.0);
        } else {
            float value = perlinNoise(position, 1, 1, seed);
            value = (value + 1.0) * 0.5;
            fragColor = vec4(vec3(value), 1.0);
        }
    }
}

// stolen from https://www.shadertoy.com/view/NlSGDz
// implementation of MurmurHash (https://sites.google.com/site/murmurhash/) for a 
// single unsigned integer.

uint hash(uint x, uint seed) {
    const uint m = 0x5bd1e995U;
    uint hash = seed;
    // process input
    uint k = x;
    k *= m;
    k ^= k >> 24;
    k *= m;
    hash *= m;
    hash ^= k;
    // some final mixing
    hash ^= hash >> 13;
    hash *= m;
    hash ^= hash >> 15;
    return hash;
}

// implementation of MurmurHash (https://sites.google.com/site/murmurhash/) for a  
// 2-dimensional unsigned integer input vector.

uint hash(uvec2 x, uint seed){
    const uint m = 0x5bd1e995U;
    uint hash = seed;
    // process first vector element
    uint k = x.x; 
    k *= m;
    k ^= k >> 24;
    k *= m;
    hash *= m;
    hash ^= k;
    // process second vector element
    k = x.y; 
    k *= m;
    k ^= k >> 24;
    k *= m;
    hash *= m;
    hash ^= k;
	// some final mixing
    hash ^= hash >> 13;
    hash *= m;
    hash ^= hash >> 15;
    return hash;
}


vec2 gradientDirection(uint hash) {
    switch (int(hash) & 3) { // look at the last two bits to pick a gradient direction
    case 0:
        return vec2(1.0, 1.0);
    case 1:
        return vec2(-1.0, 1.0);
    case 2:
        return vec2(1.0, -1.0);
    case 3:
        return vec2(-1.0, -1.0);
    }
}

float interpolate(float value1, float value2, float value3, float value4, vec2 t) {
    return mix(mix(value1, value2, t.x), mix(value3, value4, t.x), t.y);
}

vec2 fade(vec2 t) {
    // 6t^5 - 15t^4 + 10t^3
	return t * t * t * (t * (t * 6.0 - 15.0) + 10.0);
}

float perlinNoise(vec2 position, uint seed) {
    vec2 floorPosition = floor(position);
    vec2 fractPosition = position - floorPosition;
    uvec2 cellCoordinates = uvec2(floorPosition);
    float value1 = dot(gradientDirection(hash(cellCoordinates, seed)), fractPosition);
    float value2 = dot(gradientDirection(hash((cellCoordinates + uvec2(1, 0)), seed)), fractPosition - vec2(1.0, 0.0));
    float value3 = dot(gradientDirection(hash((cellCoordinates + uvec2(0, 1)), seed)), fractPosition - vec2(0.0, 1.0));
    float value4 = dot(gradientDirection(hash((cellCoordinates + uvec2(1, 1)), seed)), fractPosition - vec2(1.0, 1.0));
    return interpolate(value1, value2, value3, value4, fade(fractPosition));
}

float perlinNoise(vec2 position, int frequency, int octaveCount, uint seed) {
    float value = 0.0;
    float amplitude = 1.0;
    uint currentSeed = seed;
    for (int i = 0; i < octaveCount; i++) {
        currentSeed = hash(currentSeed, 0x0U); // create a new seed for each octave
        float octaveScaling = pow(float(frequency), float(i));
        value += perlinNoise(position * octaveScaling, currentSeed) * (amplitude / octaveScaling);
    }
    return value;
}
