// Random function
float random(vec2 co){
    return fract(sin(dot(co, vec2(12.9898, 78.233))) * 43758.5453);
}

// Noise
float noise (in vec2 st) {
    st*=1.2;
    vec2 i = floor(st);
    vec2 f = fract(st);

    float a = random(i);
    float b = random(i + vec2(1.0, 0.0));
    float c = random(i + vec2(0.0, 1.0));
    float d = random(i + vec2(1.0, 1.0));

    vec2 u = f*f*(3.0-2.0*f);

    return mix(a, b, u.x) +
            (c - a)* u.y * (1.0 - u.x) +
            (d - b) * u.x * u.x * u.y;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy - 0.5;
    uv.x *= iResolution.x/iResolution.y;

    uv.y -= 0.13;
    vec3 col = vec3(1.);
    col += vec3(0.3, 0.1, 0.);
    col -= vec3(smoothstep(length(sin(uv)*1.1*cos(uv)), .3, .32)) 
        + vec3(0., 0.13, 0.16);
    col *= vec3(smoothstep(.3, .301, length(uv)));

    col += vec3(length(noise(uv)/12.));
    col += vec3(length(noise(uv)/4.));

    uv.y += 0.4;

    uv *= 25.;

    uv.y += noise(uv);
    if ((sin((uv.x+4.4)/4.)+uv.y)*noise(uv)*2. < -.2)
    {
        col = vec3((sin((uv.x+4.4)/4.)+uv.y)*noise(uv)+0.5, 0. ,0.);
    }
    if ((sin(noise(uv)*uv.x)+uv.y) < -2.9)
    {
        col = mix(col, vec3(0.2, 0.1 ,0.1), 0.2);
    }
    if ((sin(uv.x)+uv.y)*noise(uv)*1.76 < -4.9)
    {
        col = mix(col, vec3(0.4, 0.3 ,0.3), 0.1);
    }
    uv /= 3.;

    // Output to screen
    fragColor = vec4(col,1.0);
}
