vec2 center = vec2(0.5, 0.5);


float drawCircleFragment(float dist, float opacity, float filled) {
    float _opacity = clamp(opacity, 0.0, 1.0);
    float circleR = 1.0;
    float glowR = 1.5;
    if (dist < circleR) {
        return smoothstep(0.7-filled, 1.0, dist)*0.6 + _opacity;
    } else if (dist < glowR) {
        float a0 = (dist - circleR)/(glowR - circleR);
        float a = 1.0-a0 ;
        return smoothstep(0.0, 1.0, a)*_opacity;
    }
    return 0.0;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 baseColor = vec3(0.9, 0.2, 0.9);

    float b4 = texture(iChannel0, vec2(0.0, 0.0)).x;
    float b3 = texture(iChannel0, vec2(0.3, 0.0)).x;
    float b2 = texture(iChannel0, vec2(0.6, 0.0)).x;
    float b1 = texture(iChannel0, vec2(0.6, 0.0)).x;


    float c7 = drawCircleFragment(distance(fragCoord, vec2(400,225))/(100.0 + 50.0*b1), b1+2.0, 10.0);
    float c8 = drawCircleFragment(distance(fragCoord, vec2(400,225))/(130.0 + 30.0*b2), b2, 0.0);
    float c9 = drawCircleFragment(distance(fragCoord, vec2(400,225))/(165.0 + 10.0*b3), b3 + 0.4, 0.0);
    //float c9 = drawCircleFragment(distance(fragCoord, vec2(400,225))/165.0, 1.0);
    //float c10 = drawCircleFragment(distance(fragCoord, vec2(400,225))/205.0, 1.0);


    float a = texelFetch(iChannel1, ivec2(mod(uv*32.0, 8.0)), 0).x * 0.3;

    fragColor = vec4(baseColor * (a+c7+c8+c9), 1.0);
    //fragColor = vec4(baseColor * (a+c7+c8+c9+c10), 1.0);

}
