void mainImage(out vec4 O, in vec2 I) {
    ivec2 uv = ivec2(I);
    ivec2 phase = iFrame+ivec2(0,1);

    //move mod 4 bit to last place
    ivec2 offset = phase >> 1;

    //replace last bit with offset
    //   11111110       00000001
    uv = (uv & -2) | (offset & 1);

    O = texelFetch(iChannel0,uv,0);
}
