mat2 rotate2d(float angle)
{
	return mat2(cos(angle),-sin(angle),sin(angle),cos(angle));
}

vec3 wedgwood_blue=vec3(0.08,0.51,0.67);
vec3 turquoise_blue=vec3(0.0,0.68,0.8);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord.xy - 0.5 * iResolution.xy) / min(iResolution.x, iResolution.y);

    vec2 ipos = floor(p*5.0)*rotate2d(iTime*0.5);
    vec2 fpos = fract(p*6.0)+0.3;    
    vec3 color = vec3(mod(ipos.x + ipos.y,2.0));
    if (fpos.x < 0.5) {
        color *= turquoise_blue;
    }
    else if (fpos.y < 0.5) {
        color *= wedgwood_blue;
    }
    fragColor = vec4(color, 1.0);
}
