// MIT License (c) bitrate16
#define A_CH   0.25
#define B_CH   0.75
#define R_SC   32.0
#define PERIOD 2.0
#define PI     3.14159265358979323846

float polarCurve(float angle) {
    return sin(angle * 30.0);
}

vec4 polarFrag(float angle, float radius, float ch_a, float ch_b) {
    float rads = sin(radius - iTime * 0.0);
    float radm = radius * rads;
    float polc = polarCurve(angle + 0.2 * iTime * sign(radm));
    float frag = abs(radm) * abs(polc);

    return frag * vec4(abs(sin(exp(sin(iTime * 1.5)))) * 0.43, abs(sin(exp(sin(iTime * 1.13)))) * abs(cos(rads)) * sign(polc), abs(sin(0.4 * exp(sin(iTime * 1.87)))) * sign(radm) * cos(sin(polc)), 3.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;

    // Constants
    float MAX_RADIUS = min(iResolution.x, iResolution.y) / R_SC;

    // Get radius
    vec2  radvec = fragCoord.xy - iResolution.xy / 2.0;
    float radius = length(radvec);

    // Get rotation angle [0.0 - 1.0]
    float angle  = atan(radvec.y / radvec.x) / (PI * PERIOD) + 0.25;
    if(radvec.x <= 0.0)
        angle += 0.5;
    #ifdef MIRROR
    	if(radvec.x <= 0.0)
            angle = 1.0 - angle;
    #endif

    float channel_a;
    float channel_b;
#ifdef yeah_now_this_code_will_fetch_a_texture_but_who_cares_lol
    #ifndef SWAP
    	channel_a = texture(iChannel0, vec2(angle, A_CH)).x;
    	channel_b = texture(iChannel0, vec2(angle, B_CH)).x;
    #else
    	channel_b = texture(iChannel0, vec2(angle, A_CH)).x;
    	channel_a = texture(iChannel0, vec2(angle, B_CH)).x;
    #endif
#endif

    radius /= MAX_RADIUS;
    angle *= PI;
    fragColor = polarFrag(angle, radius, channel_a, channel_b);
}
