#define pi acos(-1.0)
#define side(a) dot(uv, vec2(sin(a), cos(a)))

vec2 RotateUV(vec2 uv, float rotation) {
    return vec2(cos(rotation) * uv.x + sin(rotation) * uv.y, cos(rotation) * uv.y - sin(rotation) * uv.x); }

float triangle(vec2 uv, float size) {
    float rotation1 = pi*2.;    
	uv = RotateUV(uv, rotation1);
    uv.x = abs(uv.x);
    float w = fwidth(uv.y);
    float a = pi/3.;
    float d1 = side(a);
    a = pi*5./5.;
    float d2 = side(a); 
    float d = max(d1, d2);
    return smoothstep(w, -w, d-size); }

float Line(vec2 p, vec2 a, vec2 b) {
	vec2 pa = p - a, ba = b - a;
    float t = clamp(dot(pa, ba)/dot(ba, ba), 0.0, 1.0);
    vec2 c = a+ba*t;
    float d = length(c-p);
    float l = 0.002, g = 0.002;
	return min(1.0, 0.003/d); }

float aaStep(float edge, float gradient) {
    float halfPix = fwidth(gradient) / 2.0;
    float low = edge - halfPix;
    float hi  = edge + halfPix;
    return clamp((gradient - low) / (hi - low), 0.0, 1.0); }



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    vec3 col = vec3(0.0);
    float circle =  min(0.01/abs(length(uv + vec2(0.0, -0.0)) - 0.16), 1.0);
    float line = Line(uv, vec2(0.0, 0.35), vec2(0.0, -0.15));
    col += aaStep(mix(0.32, 0.93, uv.y), line)*vec3(1.);
    col += aaStep(0.8, circle)*vec3(1.);
    col += triangle(uv, 0.2)*vec3(1.);
    col -= triangle(uv, 0.17)*vec3(1.);
    fragColor = vec4(col,1.0);
}
