void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    bool light = false;
    vec2 p = fragCoord.xy / iResolution.xy * 6.;
    for(float i; i < 2.; i++)
    {
        p.x += sin(p.y+iTime * .05);
        p *= mat2(4, -8 ,8 ,4) / 8.;
    }

    float m = light ? smoothstep(0.0, 2.0, fract(p.x)) : smoothstep(2.0, 0.0, fract(-p.x));
    float c = 1.0 - m;
    fragColor = vec4(vec3(c), 1.0);
}
