float random (float x) {
    return fract(sin(x * 1000.0)*43758.5453123 );
}
vec2 RandomPos(){
    float x = floor(8.0*random(floor(iTime)))-3.5;
    float y = floor(8.0*random(floor(iTime+10.0)))-3.5;
    vec2 pos = vec2(x,y);
    return pos;
}


float DrawLine(float cor,float pos){
    return 1.0-step(0.025,abs(cor-pos));
}
vec3 Light(vec2 uv,vec2 pos){
    vec3 col = mix(vec3(0),vec3(1),DrawLine(uv.x,pos.x-0.5));
    col = mix(col,vec3(1),DrawLine(uv.x,pos.x+0.5));
    col = mix(col,vec3(1),DrawLine(uv.y,pos.y-0.5));
    col = mix(col,vec3(1),DrawLine(uv.y,pos.y+0.5));
    return col;
}

float CircleAni(vec2 uv, vec2 center){
    float r = pow(mod(iTime,1.5),4.0);
    float delta =(distance(uv,center)-r)*0.6;
    float degree = smoothstep(0.0,0.9,delta+0.9) - smoothstep(0.9,1.0,delta+0.9);
    if(distance(uv,center)>1.6){return 0.0;}
    return degree;
}

vec3 LinesAni(vec2 pos,vec2 uv){
    vec3 col = Light(uv,pos);
    col *= CircleAni(uv,pos);
    //col += vec3(1) * CircleAni(uv,pos);

    return col;
}

float GetGrid(float x){
    return abs(2.0*x-2.0*floor(x)-1.0);
}

vec3 BackGround(vec3 col,vec2 uv){
    float gx = GetGrid(uv.x);
    float gy = GetGrid(uv.y);

    float gridx = step(0.95,gx);
    float gridy = step(0.95,gy);

    col = mix(col,vec3(1),gridx);
    col = mix(col,vec3(1),gridy);

    return col*0.5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y*10.;
    vec3 col = vec3(0);

    col += BackGround(col,uv);

    col += LinesAni(vec2(0.5,0.5),uv);
    col += LinesAni(vec2(0.5,1.5),uv);

    fragColor = vec4(col,1.0);
}
