#define PI 3.1415926536

mat2 mr(float a) {float c=cos(a),s=sin(a); return mat2(c,s,-s,c);}
float hash(float t) {return fract(sin(t)*45398.248996);}
float hash(vec2 t) {return hash(dot(t, vec2(45.123,32.64345)));}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = iTime+25.;
    vec2 p = fragCoord/iResolution.xy;
    vec2 pp = (p-.5 - .2*cos(.2*vec2(time, time+PI/2.)));
    p /= 1.+.5*dot(pp, pp);
    p += .1;
    vec3 col = vec3(0.);
    for(float i=0.; i<32.; ++i) {
        time+=.1*hash(p+time+.1);
        p *= 1.1;
        p *= mix(vec2(1.), .5*sin(14.*p.yx + .2*time)+.5, .4);
        p *= mr(mix(.001, .05, i/32.)*time);

        int ii = int(mod(i, 3.));
        vec2 p1 = p;
        p1.x += mix(.03,.01,i/32.)*hash(p.y);
        col[ii] += pow(.002/abs(mod(p1.x, 1.)-.03), 1.5);
    }
    col = min(col, vec3(1.));
    col = mat3(
      1.,.5,.1,
      0.5,.8,0.1,
      0.3,0.2,0.8
    )*col;

    fragColor = vec4(col,1.0);
}
