#define h12(p) cos(fract(sin(dot(p, vec2(127.29, 1278.233))) * 257.541) * 127.14 + t * .5) * 3.14
#define vetor(id, q, k) dot(tanh(cos(h12(id + k) + vec2(0, 11)) * 4.), q - k)
#define rot(a) mat2(cos(a + vec4(0, 11, 33, 0)))
#define t iTime

float sdVector(vec2 p) {
    vec2  q = fract(p) - .5,
          k = floor(p);

    q *= rot(h12(k));
    return min(
               length(q) - .01, 
               length(q - vec2(clamp(q.x, - .0, .2), 0))
           ) - .01;
}

float perlin(vec2 p) {
    vec2 id = floor(p - .5),
         q  = fract(p - .5);

    float a = vetor(id, q, vec2(0, 0)),
          b = vetor(id, q, vec2(0, 1)),
          c = vetor(id, q, vec2(1, 0)),
          d = vetor(id, q, vec2(1, 1));

    vec2 u = 3. * q * q - 2. * q * q * q;

    return mix(
               mix(a, c, u.x),
               mix(b, d, u.x),
               u.y
           );
}

void mainImage(out vec4 o, vec2 u) {
    vec2 r = iResolution.xy;
         u = (u + u - r) / r.y 
                 * (1.3 + tanh(cos(t * .18) * 5.) * .3) 
                 * rot(t * .1);

    o = vec4(0);    
    vec3 k = vec3(.3, .6, .4);

    float h = perlin(u);


    // color
    if(h < 0.)
        o.rg += .02 * sin(200. * u.x) * sin(200. * u.y);
    o.b += pow(max(h, 0.), k.z * k.y);
    o.rg += pow(abs(h), k.x * k.y); 

    if(mod(t, 10.) < 2. || iMouse.z > 0.){
        // grid
        vec2 q = abs(fract(u) - .5);
        o = mix(o,
                vec4(0), 
                smoothstep(.0, 8. / r.y, max(q.x, q.y) - .5 + 6. / r.y)
            );

        // vectors
        o = mix(o, vec4(0), smoothstep(14. / r.y, .0, sdVector(u + vec2(1,2) * .002)) / 4.);
        o = mix(o, vec4(0, 0, 1, 0), smoothstep(8. / r.y, .0, sdVector(u)));
    }
}
