// Parameters
const float  GRID_SIZE       =  5.0;       // Adjust this value for larger/smaller grids
const float  LINE_WIDTH      =  0.025;
const bool   IS_LINE_SMOOTH  =  true;
const vec3   BG_COLOR        =  vec3(0.0); // Black
const vec3   LINE_COLOR      =  vec3(1.0); // White


void mainImage( out vec4 oFragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Scale UV to control the size of the grid
    vec2 gridPos = fract(uv * GRID_SIZE); // Fractional part gives position within a grid cell
    float aspect = iResolution.x / iResolution.y; // Consistent width for both the horizontal and vertical lines

    // Compute the grid line thickness
    float lineThicknessX, lineThicknessY;
    if (IS_LINE_SMOOTH)
    {
        lineThicknessX = 1.0 - smoothstep(0.0, LINE_WIDTH,          gridPos.x) * smoothstep(0.0, LINE_WIDTH,          1.0 - gridPos.x);
        lineThicknessY = 1.0 - smoothstep(0.0, LINE_WIDTH * aspect, gridPos.y) * smoothstep(0.0, LINE_WIDTH * aspect, 1.0 - gridPos.y);
    }
    else
    {
        lineThicknessX = step(gridPos.x, LINE_WIDTH)          + step(1.0 + gridPos.x, LINE_WIDTH);
        lineThicknessY = step(gridPos.y, LINE_WIDTH * aspect) + step(1.0 + gridPos.y, LINE_WIDTH * aspect);
    }

    float line = max(lineThicknessX, lineThicknessY); // Combine both directions to create the grid lines
    vec3 col = mix(BG_COLOR, LINE_COLOR, line);       // Blend grid lines with the background color

    oFragColor = vec4(col, 1.0);
}
