#define r .7 + .5 * sin(iTime)

vec2 fixUV(in vec2 c){                 // normalize UV Coord

    return (2. * c - iResolution.xy) / min(iResolution.x, iResolution.y);


}



float sdfCircle(in vec2 p){

    return length(p) - r;            // Testing points p, which are everywhere on the screen, minus the radius of the circle to    
                                     // find the boundary of the circle. So the boundary is 0, inside of the circle is negative, 
                                     // outside of the circle is positive.

}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fixUV(fragCoord);

    float d = sdfCircle(uv);

    vec3 col = vec3(.0);                                       //initialization

    col = 1. - sign(d) * vec3(.5, .3, .5);                     // make inside of the circle white, outside of the circle green-ish

    col *= 1. - exp(-3. * abs(d));                             // create a grayscale, the closer the circle boundary, the darker the color

    col *= .8 + .8 * sin(150.* abs(d));                        // create equidistance lines with sin

    col = mix(col, vec3(1.), smoothstep(.009, .004, abs(d)));  // create the white circle boundary

    vec2 m = fixUV(iMouse.xy);                                 // fix the Coord of iMouse space

    if(iMouse.z > .1){
    float CD = abs(sdfCircle(m));   // start calculating the shortest distance from cursor to the white circle boundary here

    col = mix(col, vec3(0., 1., 1.), smoothstep(.01, 0., abs(length(uv-m)-CD)));     //create a circle to test the distance from mouse cursor to circle boundary

    col = mix(col, vec3(0., 1., 1.), smoothstep(.02, 0.01, abs(length(m-uv))));

     }

    // Output to screen
    fragColor = vec4(col,1.0);
}
