#define pi 3.141592653
float scale = 3.;
float thickness = .3;

void mainImage( out vec4 O, vec2 U ){

    vec2 r = iResolution.xy;
    vec2 uv = U/r;
    vec2 cuv = (2.*U-r)/r.y;
    vec2 muv = (2.*iMouse.xy-r)/r.y;

    cuv += iTime;

    cuv *= scale;

    vec2 cell = floor(cuv);
    vec2 cellUV = (cuv - cell)*2.-1.;

    if(fract(sin(dot(cell, vec2(12.9898, 78.233))) * 43758.5453) < .5) cellUV.y *= -1.; // sin hash from Dave Hoskins

    cellUV += vec2(cellUV.x + cellUV.y > 0.?-1:1);
    float t = abs(atan(cellUV.y, cellUV.x))/pi;
    thickness *= sin(t*pi)*.8+.2;
    //thickness *= t;

    O.x = abs(
        length(cellUV)-1.
    );

    O = vec4(cos(t*pi*2.)*-.05+.8)/*(sin(t*pi*2. + vec4(0,2,4,0))*.1+.7)*/ * smoothstep(thickness,thickness + 4./r.y*scale, O.x);

}
