    float rand(vec3 n)
    {
        return fract(abs(sin(dot(n,vec3(4.3357,-5.8464,6.7645))*52.47))*256.75+0.325);
    }
    vec3 circle(vec2 uv, vec2 pos, float rad) {
        float d = length(pos - uv) - rad;
        float t = clamp(d, 0.0, 1.0);
        return vec3(0.5,0.5,1.0 - t);
    }
