float normSin(float x) {
    return (sin(x) + 1.)/2.;
}

vec4 fadeColor(float i, float t) {
    float rFactor = 1.0;
    float gFactor = 1.4;
    float bFactor = 1.3;
    return vec4(i*normSin(rFactor*t), i*normSin(gFactor*t), i*normSin(bFactor*t), 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float offSet = 20000.;
    float speed = 0.2;
    float density = 0.01;
    float zoom = 10.;

    vec2 uv = floor((2.0 * fragCoord - iResolution.xy) / zoom) / iResolution.y;
    float d = length(uv);

    float offsetTime = speed * (iTime) + offSet;
    float p = abs(sin(d*offsetTime));
    float i = density / p;
    fragColor = fadeColor(i, offsetTime);
}
