void mainImage(out vec4 O, vec2 F) {
    vec2 g = iResolution.xy,
         o = (F+F-g)/g.y;

    float l = 0., 
          f = iTime*.7-2.;

    vec3 p = vec3(o, 0.);
    float r = 0.99;

    p.z = -sqrt(max(0., r*r - dot(p.xy, p.xy)));

    vec3 n = normalize(p);
    vec2 uv = vec2(atan(n.y, n.x) / (2.*3.14159) + 0.5, acos(n.z) / 3.14159);

    for (O *= l; l++ < 55.; O += 
        .011/max(abs(uv.x + cos(l*(acos(f*.5)*.5+.6)+f)), 
                 abs(uv.y + sin(l+f)))-
        (sin(l+f*4.)*.04+.02)*(cos(l+max(abs(uv.x),abs(uv.y))*4.+vec4(0,1,2,0))+1.));
}
