//hydra
//LIVE CODING VIDEO SYNTH
//https://hydra.ojack.xyz/?sketch_id=eerie_ear_2
//out vec4 fragColor;
vec4 osc(vec2 st, float freq, float sync){
float offset = 0.0;
    float r = sin((st.x-offset/freq+iTime*sync)*freq)*0.5  + 0.5;
     float g = sin((st.x+iTime*sync)*freq)*0.5 + 0.5;
     float b = sin((st.x+offset/freq+iTime*sync)*freq)*0.5  + 0.5;
    return vec4(r, g, b, 1.0);
}

//pixelate();

vec2 pixelate(vec2 st, float pixelX, float pixelY){
    vec2 xy = vec2(pixelX, pixelY);
    return (floor(st * xy) + 0.5)/xy;
}


vec2 kaleid(vec2 _st, float nSides){
    _st -= 0.5;
    float r = length(_st);
    float a = atan(_st.x,_st.y);
    float pi = 2.*3.1416;
    a = abs(mod(a,pi/nSides));
    a = abs(a-pi/nSides/2.);
    return r*vec2(-cos(a), sin(a));
}




void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
vec2 st = fragCoord.xy/iResolution.xy; 
st +=pixelate(st,10.,10.);
st +=kaleid(st,4.);
vec4 color = osc(st.xy,30.,0.1);
fragColor = vec4(vec3(color),1.0);
}
