vec2 centerscaled(vec2 xy)
{
	float diff = iResolution.y - iResolution.x;
	float mindim = diff > 0. ? iResolution.x : iResolution.y;
	float adiff = abs(diff);
	vec2 uv = xy/mindim;
	if (diff > 0.)
	{
		uv.y-=.5*adiff/(iResolution.x);
	}
	else
	{
		uv.x-=.5*adiff/(iResolution.y);
	}
	return (uv - vec2(0.5,0.5))*2.3;
}

float explus(float a, float b) {
    //returns a well-behaved version of
    // ln(e^a + e^b)
    float big = max(a,b);
    float small = min(a,b);
    float diff = big - small;
    // ln(e^small + e^small*e^diff)
    // = ln( (1 + e^diff)*e^small)
    // = small + ln(1 + e^diff)
    if (diff > 20.) {
        return big;
    }
    return small + log(1. + exp(diff));
}

float lnmag(float n, vec2 a) {
    float lnx = log(abs(a.x));
    float lny = log(abs(a.y));
    float lnsum = explus(lnx*n, lny*n);
    float lnroot = lnsum / n;
    return exp(lnroot);
}

float nOfXY(vec2 xy) {
    float x = abs(xy.x);
    float y = abs(xy.y);
    if (max(x,y) > 1.) {
        return 1e30;
    }
    //now we want to solve the equation (x^n + y^n) = 1, for n
    //which is impossible analytically
    //so we use newton's method
    //let c = y/x
    if (x == 0.0) return 0.;
    float c = y/x;
    //now we equivalently want to solve x = 1/(C^n+1)^(1/n) for n
    //so we want to find when the function f(n) = 1/(C^n+1)^(1/n) is equal to x
    // I found that the function ln(f(1/n)) is much more well-behaved than f, so
    // we want to find an m such that ln(f(m)) = ln(x) and then return 1/m
    // therefore we want to find a zero of g(m) = m*ln(1/(C^(1/m)+1) - ln(x)
    // and g'(m) = (-ln(C^(1/m)+1)*m*C^(1/m)-ln(C^(1/m)+1)*m+ln(C)*C^(1/m))/((C^(1/m)+1)*m)
    const int iters = 20;
    float m0 = 1.;
    for (int i = 0; i < iters; i++) {
        float g = m0*log(1./(pow(c,1./m0)+1.)) - log(x);
        float gprime = (-log(pow(c,(1./m0))+1.)*m0*pow(c,(1./m0))-log(pow(c,(1./m0))+1.)*m0+log(c)*pow(c,(1./m0)))/((pow(c,(1./m0))+1.)*m0);
        m0 -= g / gprime;
    }
    return 1./m0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = centerscaled(fragCoord);
    vec4 black = vec4(0,0,0,1);
    vec4 white = vec4(1,1,1,1);
    vec2 muv = centerscaled(iMouse.xy);

    float d = lnmag(nOfXY(muv),uv);
    if (d < 1.) {
        fragColor = black;
    } else {
        fragColor = white;
    }

}
