void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 upix = 1.0/iResolution.xy;
    vec3 average = vec3(0.0);
    float range = 20.0;

    for (float i=-range; i<=range; i++) {
        for (float j=-range; j<=range; j++) {
            if (j!=0.0 && i!=0.0) {
                average += texture(iChannel0, uv+upix*vec2(j,i)).rgb;// * (1.0 - distance(uv, uv+upix*vec2(j,i)) / range);
            }
        }
    }

    average /= pow(range*2.0, 2.0)-1.0;

    vec3 col = average;

    /*
    if (texture(iChannel0, uv).r>=0.5) {
        col = vec3(1.0);
    }
    */

    // Output to screen
    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord/iResolution.xy-vec2(0.5))*vec2(iResolution.x/iResolution.y, 1.0);
    vec3 col = vec3(0.5);

    if (length(uv+vec2(0.5,0.0)) < 0.25) {
        col = vec3(0.25);
    }

    if ((uv-vec2(0.5,0.0)).x < 0.25 && (uv-vec2(0.5,0.0)).y < 0.25 && (uv-vec2(0.5,0.0)).x > -0.25 && (uv-vec2(0.5,0.0)).y > -0.25) {
        col = vec3(0.25);
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}
