#define R2D(a) mat2( cos( a + vec4(0,11,33,0 )))

void mainImage( out vec4 o, in vec2 FC)
{
float i,j,d=.1,e,t=iTime; vec3 p,q=vec3(1,-1.05,1),r=iResolution;
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*r.xy)/r.y,d)*16.;
    p.yx*=R2D(4.71);

    d=min(max(p.y,p.z),p.x);
    d-=dot(p,q)/1e4;
    e-=d*sin(t/d);

}o+=vec4(exp(p/e/q),.1)*12./i;
}
