//move points across image
int modi(int a,int b)
{
    return a - (a/b)*b;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y;
    float times[4*6] = float[](
        0.57,0.77,0.32,0.857,0.41,0.34,
        0.47,0.75,0.43,0.758,0.31,0.45,
        0.37,0.73,0.54,0.875,0.21,0.56,
        0.27,0.71,0.65,0.785,0.11,0.67
    );
    float c[3];
    for(int i =0; i < 12;i+=1)
    {
        vec2 point = vec2((sin(times[i*2]*iTime)*0.5+0.5)*iResolution.x/iResolution.y,cos(times[i*2+1]*iTime)*0.5+0.5);
        point = point*0.9 + vec2(0.05*iResolution.x/iResolution.y,0.05);
        c[modi(i,3)] += float(distance(uv,point) < 0.05);
    }
    fragColor = vec4(c[0],c[1],c[2],1.0);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
