void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = vec3(0.0);
    float bright = 0.35;
    for (int i = 0; i < 3; i++)
	{
		float f = 1.0;
		vec2 st =  f * (uv * vec2(-3.5, 0.05)+vec2(-iTime*0.1+uv.y*0.41, iTime*0.07));//y*.2 wind
		vec2 noise = texture(iChannel0, st * 0.5).rg;
		f = noise.r + noise.g;
		f = clamp(pow(abs(f)*0.5, 25.0) * 10.0, 0.00, uv.y*0.4+0.05); // Y alpha

		col += f*bright;
	}

    fragColor = vec4(col, 1.);
}
