float GetMonochromeAbsValue(in vec3 col)
{
    return step(0.5f, (col.r + col.g + col.b) / 3.0f);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 baseUv = uv;

    float circleR = 0.2f;
    vec2 centerP = vec2(0.5f, 0.5f);
    vec2 dir = uv - centerP;
    vec2 avrDir = normalize(dir) + iTime / 8.0f; //naming is wrong
    avrDir *= circleR;
    uv -= avrDir;

    vec2 texelSize = (vec2(1.0f) / iChannelResolution[0].xy) * 2.0f;

    // making outline
    vec3 center = texture(iChannel0, uv).rgb;
    vec3 right  = texture(iChannel0, vec2(uv.x - texelSize.x, uv.y)).rgb;
    vec3 top    = texture(iChannel0, vec2(uv.x, uv.y - texelSize.y)).rgb;
    vec3 diag   = texture(iChannel0, vec2(uv.x - texelSize.x, uv.y - texelSize.y)).rgb;

    float c1 = GetMonochromeAbsValue(center);
    float c2 = GetMonochromeAbsValue(right);
    float c3 = GetMonochromeAbsValue(top);
    float c4 = GetMonochromeAbsValue(diag);
    float avrC = (c1 + c2 + c3 + c4) / 4.0f;

    float dR = abs(c1 - c2);
    float dT = abs(c1 - c3);
    float dD = abs(c1 - c4);

    float d = max(dR, max(dT, dD));
    //adding noise to outline 
    vec3 outlineMask = vec3(d, d, d);


    float noiseOutline = texture(iChannel1, uv - dir).x;
    // Output to screen
    vec3 texColor = texture(iChannel0, uv).rgb;
    vec3 resOutline = outlineMask * texColor * noiseOutline;

    fragColor = vec4(resOutline + texColor, 1.0f);
    //fragColor = vec4(vec3(avrNoiseTex), 1.0f);
}
