vec4 Mountain(vec2 uv) {
    float h = mountainFunc(uv.x + iTime);

    float al = step(uv.y, h);
    vec4 color = vec4(0.44, 0.31, 0.19, 1.) * al * abs(uv.y);
    return color;

}

vec4 Sky(vec2 uv) {
    float h = mountainFunc(uv.x + iTime);

    vec2 grid = fract((uv * 5.)) - .5;
    vec2 col_id = floor(grid * 10.);
    float d = smoothstep(.9, 1., 1. -length(grid));

    d *= abs(sin(iTime)) + .5 ;

    float al = step(h, uv.y);
    vec4 color = (vec4(0.14, 0.23, 0.26, 1.) + d) * al; 
    return color;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv -= .5;
    uv.x *= iResolution.x / iResolution.y;
    uv *= 2.;
    vec2 uv0 = uv;    

    vec4 color = vec4(0., 0., 0., .0);



    vec4 mountain = Mountain(uv);
    vec4 sky = Sky(uv);

    color += mountain + sky;

    fragColor = color;
}

float sinFunc(float x, float A, float B, float C, float D) {
    return A*sin(B*(x - C)) + D;
}

float mountainFunc(float x) {
    return sinFunc(x, .1, 2., 1., 0.) + sinFunc(x, .2, 5., 1., 0.) + sinFunc(x, .2, 2., 1., 0.);
}

float rand(vec2 co){
    return fract(sin(dot(co, vec2(12.9898, 78.233))) * 43758.5453);
}
