// from https://www.shadertoy.com/view/Xst3Dj
#define P (3.1415926/2.)
#define R(a) mat2(cos(a), sin(a), -sin(a), cos(a))
#define T(v) texture(iChannel0, (u + (v))/iResolution.xy) // normalize(v) or sign(v)

void mainImage(out vec4 O, vec2 u){
        vec2 v = vec2(1) , a = v-1., t;
        vec3 b=vec3(0), c=b, p, k;
        for(int i=0;i<4;i++)
        {                
                //                v =    ( 1, 1) (-1, 1) (-1,-1) ( 1,-1)
                vec3 x,f;
                // 对角线 X
                x = T(-v).xyz; // rot 4/4*PI = (-1,-1) ( 1,-1) ( 1, 1) (-1, 1)
                // 中央十字 +
                //                  1.5
                f = T(normalize(R(P*5.)*v)).xyz; // rot 3/4*PI = ( 0, -1) ( 1, 0) ( 0, 1)  (-1, 0)     ????????       10/4            
                //f = T(.5*(R(P)*v-v)).xyz;
                b += f;
                c += x;
                a += (.7 * x.yy               
                    + .7 * T(R(P)*v).xx  // rot 2/4*PI
                    +  (i%2<0 /////////// < 1  It's a funny result of an error in golf code.
                           ? f.yx
                           : f.xy
                      )) * v;
                //v = round(v.yx*vec2(-1,1)) ;
                v *= R(P);  // rot PI/2 or -PI/2
        }
        // 读中心
        p = T(0.).xyz;
        k  = (-20.*p + 4.*b + c)/6.;

        t  = .24 * k.xy - 0.06 * normalize(p.xy) *  k.z + (1. - 0.08 * a.y) * p.xy ;
        t = R( sign(a.x) * pow(abs(a.x), .2) / 4. ) * t ;

        if(iFrame<10) {
                O = texture(iChannel1, u/iResolution.xy) - .5;
        } else {
                O = clamp(vec4(t,a.y,1), -1., 1.);
        }
}
