void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 uv = fragCoord/iResolution.xy;

  // vec2 offset = vec2(5);
  vec2 offset = vec2(5, 0.0);
  vec4 col = vec4(0.0);
  col.a = texture(iChannel0, uv).a;
  col.g = texture(iChannel0, uv - (offset/iResolution.xy)).g;
  // col.r = texture(iChannel0, uv - (offset/iResolution.xy)).r;
  // col.g = texture(iChannel0, uv).g;
  col.r = texture(iChannel0, uv).r;
  col.b = texture(iChannel0, uv - (offset/iResolution.xy)).b;
  fragColor = col;
}
