void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv*=20.;
    float y = sin(uv.x+iTime*5.); //0 - 1
    float y2 = sin(uv.y+iTime*5.); //0 - 1
    float y3 = sin(uv.x+uv.y+iTime*5.);
    float y4 = cos(uv.x+uv.y+iTime*5.);

    // Time varying pixel color
    vec3 color = vec3(y*y2,y2*y3,y3*y)*50.0;

    // Output to screen
    fragColor = vec4(color,1.0);
}
