float random (vec2 st) {
    return fract(sin(dot(st.xy,
                         vec2(12.9898,78.233)))*
        43758.5453123 );
}
float random1 (float y){
    return fract(sin(y+200.)*43758.5453123);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y*10.;

    // Time varying pixel color
    float rnd = random(floor(uv ));
    float ra = random(vec2(iTime * 0.1));
    // Output to screen
    vec2 ipos = floor(uv);
    vec2 fpos = fract(uv);

    vec3 color = vec3(rnd)*2.*ra;
    color = vec3(ipos,0.);
    color = vec3(fpos,0.);
    float r;
    float b = (mod(floor(uv.y *10.),2.)-0.5)*2.;
    r = random1(floor(10.*(uv.x+b*random1(floor(uv.y*10.))*iTime))*0.1);
    //random1(floor(uv.y*10.))
    float degree = step(-r,-0.3);
    color = vec3(degree);
    fragColor = vec4(color,1.0);
}
