#define Main void mainImage(out vec4 Q, in vec2 U)
#define ei(a) mat2(cos(a),-sin(a),sin(a),cos(a))
#define Z .5
vec3 hash(vec3 p3)
    {
        p3 = fract(p3 * vec3(.1031, .1030, .0973));
        p3 += dot(p3, p3.yxz+33.33);
        return fract((p3.xxy + p3.yxx)*p3.zyx);

    }

