Main {
    Q = vec4(0);
    vec4 QB = vec4(0);
    for (float x=-3.;x<=3.;x++)
    for (float y=-3.;y<=3.;y++)
    {
          vec2 xy = vec2(x,y);
          vec4 b = B(U+xy);
          if (b.w>0.) {
              vec4 a = A(U+xy);

              vec4 _1 = a, _2 = a;
              mat2 m = ei(6.2*hash(vec3(U+xy,iTime)).x);
              if (b.w>1.) {
                  vec2 v = vec2(0,Z)*m;
                  _2.xy -= v;
                  _1.xy += v;
              }
              if (_1.x<U.x+.5&&_1.x>=U.x-.5&&
                  _1.y<U.y+.5&&_1.y>=U.y-.5){
                  Q += .5*b.w*_1;
