float rect(vec2 st, vec2 size)
{
    size = 0.25 * (1.0 - size);

    vec2 uv = step(size, st * (1. - st));

    return uv.y * uv.x;
}

float square_wave(float t, float period)
{
    return step(period, mod(t, 2.0 * period));
}
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 st = fragCoord.xy / iResolution.xy;

    vec3 influenced_color = vec3(0.548, 0.565, 0.542);
    vec3 influencing_color_A = vec3(0.295, 0.295, 0.295);
    vec3 influencing_color_B = vec3(0.904, 0.947, 0.965);

    float period = 3.0;

    vec3 color = mix(influencing_color_A, influencing_color_B, st.y);

    color *= square_wave(iTime, period);
    color = mix(color, influenced_color, rect(st, vec2(0.070, 0.740)));

    fragColor = vec4(color, 1.0);
}
