void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 uv_2 = (uv*2.-1.)*vec2(1.,iResolution.y/iResolution.x);
    float l = length(uv_2);
    uv_2 = vec2(1./l + iTime, atan(uv_2.y/uv_2.x)+iTime*.2);

    vec4 col = texture(iChannel0, uv_2) * l;
    col *= l;

    fragColor = col;
}
