#define I iResolution
#define A min( I.x, I.y )
#define rot( a ) mat2( cos(a), sin(a), -sin(a), cos(a) )
#define sp( p ) length(p)-.3
#define l( a ) smoothstep( -2. / A, 2. / A, mod( a, .5 ) - .25 )
#define h(u) fract(dot( floor(u.yxx * 2.45), sin(u.xyy+vec3(1,2,3)) / 4.5 ) )

vec2 t;

void up( inout vec3 p ){
    p.xy = mod( p.xy + .35, .7 ) - .35;
    p.z -= h( (t - 5.) ) + cos( t.x + iTime ) * .7 - cos( t.y - iTime ) * .5;
    p.xz *= rot( iTime - t.y + t.x );
    p.xy *= rot( iTime * 2. + t.y * t.x );
}

float df( vec3 p ){

    t = round( p.xy / .7 );
    up( p );

    return sp( p );
}

void mainImage( out vec4 U, in vec2 V )
{
    vec2 u = (V * 2. - I.xy) / A;

    float i, t, d;

    vec3 c, p, o = vec3( 0, 0, -20 ), r = vec3( u * .1, 1. );

    while( i++ < 16. )
        p = o + r * t,
        d = df(p),
        t += d;

    if( d < 1e-3 ){
        up( p );
        c += l( p.y * 5. ) * 8. * dot( vec3( -1, 2, 3 ), p ) * .1;
    }


    U = vec4(c,1.0);
}
