/*
by Dom Mandy in 2024
*/
void mainImage(out vec4 P, vec2 V) {
    vec2 Z = iResolution.xy,
         C = V = 2.7 * (Z - V - V).yx / Z.y;

    float v, x, y,
          z = y = x = 9.,
          a = atan(C.y, C.x), 
          d = dot(C, C);

    for (int k; k < 99; k++) {
        float c = dot(V, vec2(a, log(d) / 2.));
        V = exp(-a * V.y) * pow(d, V.x / 2.) * vec2(cos(c), sin(c));
        V = vec2(dot(V, C), dot(vec2(V.y, -V.x), C)) / d;

        x = min(x, abs(V.x));
        y = min(y, abs(V.y));
        z > (v = dot(V, V)) ? z = v, Z = V : Z;
    }

    x = smoothstep(1., -13., log(x));
    y = smoothstep(1., -13., log(y));
    z = smoothstep(-55., 1., log(z));
    z = z * y * x;
    P = z * z + sqrt(z + z * cos(atan(Z.y, Z.x) - vec4(0, 2.1, 4.2, 0)));
}
