void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;


    // Output to screen
    fragColor = texture(iChannel0, uv);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{



    vec2 uv = (2.*fragCoord-iResolution.xy) / iResolution.y;
    vec2 mouse = (2.*iMouse.xy-iResolution.xy) / iResolution.y;

    bool mouse_down = iMouse.z > 0.;

    vec4 color = texelFetch(iChannel0, ivec2(fragCoord), 0);


    if(iMouse.z > 0. && abs(length(iMouse.xy-fragCoord)) < 1.) 
    {
        color = vec4(1.,1.,1.,1.);
    }

    if (
        texelFetch(iChannel0, ivec2(fragCoord) + ivec2(0, 1),0).x > 0.
    ) {
        color = vec4(1., .5, .5, 1.);
    }

    fragColor = color;



}
