float sdBox( in vec2 p, in vec2 b ) {
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}
float sdTri( in vec2 p, in vec2 q ) {
    p.x = abs(p.x);
    vec2 a = p - q*clamp( dot(p,q)/dot(q,q), 0.0, 1.0 );
    vec2 b = p - q*vec2( clamp( p.x/q.x, 0.0, 1.0 ), 1.0 );
    float s = -sign( q.y );
    vec2 d = min( vec2( dot(a,a), s*(p.x*q.y-p.y*q.x) ),
                  vec2( dot(b,b), s*(p.y-q.y)  ));
    return -sqrt(d.x)*sign(d.y);
}
float opUnion( float d1, float d2 ) { return min(d1,d2); }
float opRound( float dist, in float r ) { return dist - r; }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord/iResolution.y - 0.5)*2.0;
    fragColor = vec4(uv, 0.0, 1.0);

    uv = (uv*3.0) - vec2(1.5, -0.1*iResolution.y/iResolution.x);

    float width = iResolution.x/iResolution.y;
    float center_x = (1.0 - width);

    float     ROUND = 0.185;
    float TRI_ROUND = 0.055;
    float     RATIO = 0.75;
    float tip_width = 0.135 * width;

    float dist = opUnion(
        sdBox(uv - vec2(-center_x,  1.0 -     RATIO), vec2(    width, 0.8              ) - ROUND) - ROUND,
        sdTri(uv - vec2(-center_x, -1.0 + TRI_ROUND), vec2(tip_width, 2.0*(1.0 - RATIO))        ) - TRI_ROUND
    );


    vec3 col = vec3(1.0 - smoothstep(0.0, 17.0/iResolution.x, dist));

    fragColor = vec4(col, 1.0);
}
