vec2 Cmult(vec2 a,vec2 b){
     float r1 = length(a);
     float r2 = length(b);
     float theta1 = atan(a.y,a.x);
     float theta2 = atan(b.y,b.x);
     float r = r1 * r2;
     float theta = theta1+theta2;
     return r * vec2(cos(theta),sin(theta));
}
//derivative 
vec2 d(vec2 z){
    return Cmult(vec2(2.,0),z);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy - .5;
    uv.x *= iResolution.x/iResolution.y;
    uv *= 1.5;

    vec3 col = vec3(.1,.1,.1);
    vec2 z = uv;
    vec2 c = uv;
    int i = 0;

    int iterations = 4000;

    for(int i = 0;i < iterations;i++){
        z = vec2(z.x * z.x - z.y * z.y,2. * z.x * z.y) + c;
        //exit conditions
        if(length(z) > 2. || length(d(z)) >2.)break;

    }
    //check the length of the derivative

    if(length(d(z)) < 1.){
        col.gb = vec2(1.);
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}
