void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = texture(iChannel0, uv).rgb;

    col *= 0.5 + 0.5 * cos(iTime + uv.xyx + vec3(0, 2, 4));

    // Output to screen
    fragColor = vec4(col,1.0);
}

void calc(ivec2 coords, inout bool value, ivec2 dir) {

    ivec2 coords2 = coords - dir;
    if (coords2.x < 0 || coords2.y < 0) {
        return;
    }

    bool value2 = texelFetch(iChannel0, coords2, 0).r > 0.5;

    value = value ^^ value2;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    ivec2 coords = ivec2(fragCoord);
    if (iFrame == 0) {
        fragColor = vec4(0, 0, 0, 1);
        return;
    }
    if (coords == ivec2(0, 0)) {
        fragColor = vec4(1);
        return;
    }

    bool value = texelFetch(iChannel0, coords, 0).r > 0.5;
    calc(coords, value, ivec2(1, 0));
    calc(coords, value, ivec2(0, 1));

    fragColor = vec4(vec3(value), 1.0);
}
