//米字符
float Band(vec2 uv,float w){
    float heng = 
        smoothstep(w/2.0,w/2.0-0.01,uv.y)*smoothstep(-w/2.0,-w/2.0+0.01,uv.y);
    float shu = 
        smoothstep(w/2.0,w/2.0-0.01,uv.x)*smoothstep(-w/2.0,-w/2.0+0.01,uv.x);
    float xw = w*sqrt(2.0);
    float xier = 
        smoothstep(xw/2.0+uv.x,xw/2.0+uv.x-0.01,uv.y)*smoothstep(uv.x-xw/2.0,uv.x-xw/2.0+0.01,uv.y);
    float xiel = 
        smoothstep(xw/2.0-uv.x,xw/2.0-uv.x-0.01,uv.y)*smoothstep(-uv.x-xw/2.0,-uv.x-xw/2.0+0.01,uv.y);
    return heng+shu+xier+xiel;

}
//圆
float Circle(vec2 uv,float outr,float inr)
{
    float d = length(uv);
    float r1 = smoothstep(outr,outr-0.01,d);
    float r2 = smoothstep(inr,inr-0.01,d);
    return r1-r2;
}
float Animation(vec2 uv)
{
   float constAngle = atan(1.0);
   float tangle = radians(315.2+iTime*70.0);
   int t =7-int(tangle/constAngle)%8;
   float angle = atan(uv.y,uv.x)+ 2.0 * 3.1415926;
   int i = int(angle/constAngle)%8;
   float v = (Circle(uv,0.4,0.3) - Band(uv,0.06))*0.5;
   if(t==i) v *=2.0;
   return v;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x/iResolution.y;
    float v = Circle(uv,0.4,0.3) - Band(uv,0.06);

    // Output to screen
    fragColor = vec4(vec3(Animation(uv)),1.0);
}
