// Fork of "better template" by ProfessorPixels. https://shadertoy.com/view/l3yGRV
// 2024-06-12 22:01:37
float tube(vec2 p){return -length(p)+20.;}
vec3 march(vec3 t,vec3 s)
{
    vec3 p = s;
    for (float d=0.;d<1600.;d+=tube(p.xy))
    {
        p+=t*tube(p.xy);
        if (tube(p.xy)<.02) break;
    }
    return p;
}
void mainImage(out vec4 O,vec2 F )
{
    float f = 10.;
    F-=iResolution.xy*.5;
    vec3 p = march(normalize(vec3(F/iResolution.xy,-f)),vec3(0,0,-f));
    p.x=atan(p.y/p.x)/3.1416;
    p.z*=.001;
    O=texture(iChannel0,p.xz-vec2(0,iTime*.1))*vec4(.4,.5,.5,0);
    O=O*clamp(1.-(-p.z*.8-.3),0.,1.)+clamp(texture(iChannel1,(F+10.*sin(iTime+vec2(0,1.7)))*.001).r+texture(iChannel2,F*.05).r,0.,2.)*.5*clamp(-p.z*.8-.3,0.,1.);
}
