void mainImage(out vec4 o, in vec2 f)
{
    vec2 r = 2./iResolution.xy;

    f = f*r - 1. // pixel to NDC
    -((iMouse.z == 1. || iMouse.x != 0.)? // pixel - mouse?
        iMouse.xy*r - 1. // mouse NDC
    :vec2(0)); // init mouse to 0,0

    float T = iTime*.1,
        W = T + abs(f.x*f.y*4.) + sin(sin(T)*dot(f,f)*4.), // wave
        v = sin(W*9.);
    o = vec4((.5 + .5*cos(W + vec3(0,2,4))) // RGB waves
        *smoothstep(1.,-1.,(abs(v)-.5)/fwidth(v)), // Black edges
        1 // alpha
    );
}
