const float timeSpeed = 0.02;
const vec2 direction1 = vec2(-2., -1.);
const vec2 direction2 = vec2(-2., 1.);
const float noiseLerp = 0.1;
const vec2 scale1 = vec2(0.3, 0.5);
const vec2 scale2 = vec2(0.3, 0.6);

const mat2 m = mat2( 1.6,  1.2, -1.2,  1.6 );
float noise( in vec2 p ) {
       return texture(iChannel0, p * 0.05).r - 0.1;
}

float fbm(vec2 p) {
    float a =1.;
    float f = 1.;
    return a*noise(p) * 0.5
        + a*0.3 * noise(p*f*2. ) 
        + a*0.25 * noise(p*f*4. )
        + a*0.1 * noise(p*f*8. );
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.y;


    float time = iTime * timeSpeed ;
    float n1 = fbm(uv * scale1  + direction1 * time);
    float n2 = fbm(uv * scale2 + direction2 * time);
    float lerp = noiseLerp + (fbm(uv * vec2(0.7, 0.9) - vec2(time * 2., 0.) ) - 0.2);
    float c = mix(n1, n2, lerp) * 1.2 ;
    c = pow(c, 2.);
    vec3 col = mix(vec3(0.6, 0.75, 0.9), vec3(1., 1., 1.), c);

    fragColor = vec4(col,1.0);
}
